package api.tests;

import api.endpoints.UserEndpoints;
import api.payload.User;
import io.restassured.http.ContentType;
import io.restassured.response.Response;

import api.payload.User;
import com.github.javafaker.Faker;
import org.testng.Assert;
import org.testng.annotations.BeforeClass;
import org.testng.annotations.Test;

public class UserTests {

    Faker faker;
    User userPayload;

    @BeforeClass
    public void setup(){

        faker = new Faker();
        userPayload = new User();

        userPayload.setId(faker.idNumber().hashCode());
        userPayload.setFirstName(faker.name().firstName());
        userPayload.setLastName(faker.name().lastName());
        userPayload.setUsername(faker.name().username());
        userPayload.setPassword(faker.internet().password(5, 10));
        userPayload.setEmail(faker.internet().safeEmailAddress());
        userPayload.setPhone(faker.phoneNumber().phoneNumber());
        userPayload.setUserStatus(1); // Set a default userStatus

    }

    @Test(priority = 1)
    public void test_createUser(){

        Response response = UserEndpoints.createUser(userPayload);
        System.out.println("DEBUG: "+ userPayload.getUsername());

        response.then().log().all();

        Assert.assertEquals(response.getStatusCode(), 200);

    }

    @Test(priority = 2)
    public void test_getUserByName() {

        Response response = UserEndpoints.getUser(this.userPayload.getUsername());
        System.out.println("DEBUG: "+ this.userPayload.getUsername());

        response.then().log().body();

        Assert.assertEquals(response.getStatusCode(), 200);
    }

    @Test(priority = 3)
    public void test_modifyUser(){

        userPayload.setFirstName(faker.gameOfThrones().character());
        userPayload.setLastName(faker.name().lastName());
        userPayload.setEmail(faker.internet().safeEmailAddress());

        Response response = UserEndpoints.updateUser(userPayload.getUsername(), userPayload);
        response.then().log().all();

        Assert.assertEquals(response.getStatusCode(), 200);

        Response updatedUser = UserEndpoints.getUser(userPayload.getUsername());
        Assert.assertEquals(response.getStatusCode(), 200);

    }

}

